def Pr_HE1(s:str) -> list[str] :
    result = []
    current_string = []
    current_depth = 0

    for c in s:
        if c == '(':
            current_depth += 1
            current_string.append(c)
        elif c == ')':
            current_depth -= 1
            current_string.append(c)

            if current_depth == 0:
                result.append(''.join(current_string))
                current_string.clear()

    return result

