def check_pre_solution_HE1(s:str) -> bool:
    if len([c for c in s if c != '(' and c!=')']) > 0 :
        return False
    stack = 0 # keep track of how many '(' found so far wich have not been closed
    for c in s:
        if c == ')' and stack==0: return False
        elif c == ')':
            stack = stack-1
        else :
            stack = stack+1
    return stack == 0

