        if len([c for c in segment if c != '(' and c!=')']) > 0 :
            return False
        stack = 0 # keep track of how many '(' found so far wich have not been closed
        while len(segment) > 0 :
            c = segment[0]
            segment = segment[1:]
            if c == ')' and stack==0: return False
            elif c == ')':
                stack = stack-1
                if stack == 0 and len(segment)>0 : return False
            else :
                stack = stack+1
        return stack == 0


