def Pr_HE120(A:list[float], k:int) -> list[float] :
    segments = []
    for i in range(len(A)):
        for v in range(i+1,len(A)+1):
            z = A[i:v]
            if z == sorted(z) : segments.append(z)
    longest = []
    for z in segments:
        if len(z) == k: return z
        if len(z) > len(longest) : longest = z
    return longest

