def check_post_solution_HE120(r:list[float], A:list[float], k:int) -> bool :
    if len(r) > k :
        return False
    segments = []
    for i in range(len(A)):
        for y in range(i+1,len(A)+1):
            z = A[i:y]
            if z == sorted(z) : segments.append(z)
    return r in segments  and len(r) == max([len(z) for z in segments if len(z) <= k])

