def Pr_HE13(s:list[int]) -> int :
    smallest_x = min(s)
    if smallest_x <= 1: return 1
    greatest = 1
    for d in range(1, smallest_x + 1):
        if all([ x%d == 0 for x in s]):
            greatest = d
    return greatest

